var _ = require('underscore');
var util = require('util');

var OAE = require('oae-util/lib/oae');

var SearchAPI = require('oae-search');
var SearchUtil = require('oae-search/lib/util');

var REGEX_SEARCH_ENDPOINT = /\/api\/search\/([^\/]+)(\/.*)?/;


/**
 * @REST postSearchReindexAll
 *
 * Re-index all data
 *
 * @Server      admin
 * @Method      POST
 * @Path        /search/reindexAll
 * @Return      {void}
 * @HttpResponse                        200                 Reindexing task queued
 * @HttpResponse                        401                 Only global administrator can trigger a full reindex
 */
OAE.globalAdminRouter.on('post', '/api/search/reindexAll', function(req, res) {
    SearchAPI.postReindexAllTask(req.ctx, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        return res.send(200);
    });
});

/**
 * @REST getSearch
 *
 * Perform a search
 *
 * @Server      admin,tenant
 * @Method      GET
 * @Path        /search/{searchType}
 * @PathParam   {string}                searchType              The type of search being performed                                                                      [content-library,followers,following,general,members-library,memberships-library]
 * @QueryParam  {number}                [limit]                 The maximum number of search results to return
 * @QueryParam  {string}                [q]                     The search query
 * @QueryParam  {string}                [sort]                  The sort direction. Defaults to asc                                                                     [asc,desc]
 * @QueryParam  {number}                [start]                 The document index from which to start. Defaults to 0
 * @QueryParam  {string}                [scope]                 Only applicable to `general` search. Specifies what filter scope to use. Should be one of _all, _network, _share, _tenant or a specified tenant alias
 * @QueryParam  {boolean}               [resourceCanManage]     Only applicable to `_share` scope. Specifies if you can manage the resource being shared
 * @QueryParam  {string}                [resourceTenantAlias]   Only applicable to `_share` scope. The alias of the tenant that the resource being shared belongs to
 * @QueryParam  {string}                [resourceVisibility]    Only applicable to `_share` scope. The visibility of the resource being shared                          [private, loggedin, public]
 * @Return      {SearchResponse}                                The retrieved search results
 * @HttpResponse                        200                     Search results available
 * @HttpResponse                        400                     An invalid or unknown search type was specified
 */
var _handleSearchRequest = function(req, res) {
    var searchType = req.params[0];
    if (searchType) {
        req.telemetryUrl = util.format('/api/search/%s', searchType);
    }

    var pathParams = req.params[1] ? _.compact(req.params[1].split('/')) : [];
    var opts = _.extend({}, req.query, SearchUtil.getSearchParams(req), {'pathParams': pathParams});
    SearchAPI.search(req.ctx, searchType, opts, function(err, result) {
        if (err) {
            return res.send(err.code, err);
        }

        res.send(200, result);
    });
};

OAE.tenantRouter.on('get', REGEX_SEARCH_ENDPOINT, _handleSearchRequest, '/api/search');
OAE.globalAdminRouter.on('get', REGEX_SEARCH_ENDPOINT, _handleSearchRequest, '/api/search');
