var _ = require('underscore');

var OAE = require('oae-util/lib/oae');

var SearchAPI = require('oae-search');
var SearchUtil = require('oae-search/lib/util');

/**
 * _GET_ `/api/search/[searchType][/path/separated/params]`
 *
 * Perform a general search for resources in the database.
 *
 * @param   {String}        resourceType    The type of resources to search. Can be one of: `all`, `content`, `group` or `user`. If not a valid resource type, will default to `all`
 * @param   {String}        [q]             A full-text search that can be input to filter documents by a text match (Default: * for all documents)
 * @param   {Number}        [limit]         The maximum number of results to return (Default: 10)
 * @param   {Number}        [start]         The document at which to start (Default: 0)
 * @param   {String}        [sort]          Which direction to sort the results. Can be one of: `asc` or `desc`. If an invalid sort value is provided, it defaults to `asc`
 * @return  {SearchResult}                  The response, if successful, will be a search result object as defined by `SearchModel.SearchResult`
 */
OAE.tenantServer.get(/\/api\/search\/([^\/]+)(\/.*)?/, function(req, res) {
    var searchType = req.params[0];
    if (searchType) {
        req.telemetryUrl = '/api/search/' + searchType;
    }

    var params = req.params[1] ? _.compact(req.params[1].split('/')) : [];
    var opts = _.extend({}, req.query, SearchUtil.getSearchParams(req), { 'params': params });

    SearchAPI.search(req.ctx, searchType, opts, function(err, result) {
        if (err) {
            res.send(err.code || 500, err);
        } else {
            res.send(200, result);
        }
    });
});

/**
 * _POST_ `/api/search/_refresh`
 *
 * Manually refreshes the OAE search index, ensuring that searches are up-to-date with the most recent documents.
 */
OAE.tenantServer.post('/api/search/_refresh', function(req, res) {
    req.telemetryUrl = '/api/search/_refresh';
    SearchAPI.refresh(req.ctx, function(err) {
        if (err) {
            res.send(err.code || 500, err.msg);
        } else {
            res.send(200);
        }
    });
});


