    describe('General Search Parameters', function() {

        /**
         * Verifies empty, null, and valid values for both single and array lookups using the resourceTypes parameter.
         */
        it('verifies a variety of valid and invalid values for the resourceTypes parameter', function(callback) {
            var jackUsername = TestsUtil.generateTestUserId('jack');
            var groupAlias = TestsUtil.generateTestUserId('group');

            /*!
             * Helper function that verifies that a search result feed has (or doesn't have) results of certain resourceTypes
             *
             * @param  {SearchResult}   results             The search results object
             * @param  {Boolean}        shouldHaveUser      Whether or not the results should contain a user object
             * @param  {Boolean}        shouldHaveGroup     Whether or not the results should contain a group object
             * @param  {Boolean}        shouldHaveContent   Whether or not the results should contain a content object
             * @return {Object}                             The search document. `null` if it didn't exist
             */
            var _verifyHasResourceTypes = function(results, shouldHaveUser, shouldHaveGroup, shouldHaveContent) {
                var hasUser = false;
                var hasGroup = false;
                var hasContent = false;
                _.each(results.results, function(doc) {
                    if (doc.resourceType === 'user') {
                        hasUser = true;
                    } else if (doc.resourceType === 'group') {
                        hasGroup = true;
                    } else if (doc.resourceType === 'content') {
                        hasContent = true;
                    }
                });

                assert.equal(shouldHaveUser, hasUser);
                assert.equal(shouldHaveGroup, hasGroup);
                assert.equal(shouldHaveContent, hasContent);
            };

            // Ensure at least one user, group and content item exists
            RestAPI.User.createUser(camAdminRestContext, jackUsername, 'password', 'Jacker', null, function(err, jack) {
                assert.ok(!err);
                var jackRestContext = TestsUtil.createTenantRestContext(global.oaeTests.tenants.cam.host, jackUsername, 'password');

                RestAPI.Group.createGroup(jackRestContext, groupAlias, groupAlias, 'A really awesome group', 'public', 'no', [], [], function(err, group) {
                    assert.ok(!err);

                    RestAPI.Content.createLink(jackRestContext, 'Apereo Foundation', 'Link to Apereo Foundation Website', 'public', 'http://www.apereo.org', [], [], function(err, content) {
                        assert.ok(!err);

                        // Verify unspecified resourceTypes searches all
                        SearchTestsUtil.searchAll(jackRestContext, 'general', null, null, function(err, results) {
                            assert.ok(!err);
                            _verifyHasResourceTypes(results, true, true, true);

                            // Verify empty resourceTypes searches all
                            SearchTestsUtil.searchAll(jackRestContext, 'general', null, {'resourceTypes': ''}, function(err, results) {
                                assert.ok(!err);
                                _verifyHasResourceTypes(results, true, true, true);

                                // Verify non-matching single resource type returns nothing
                                SearchTestsUtil.searchAll(jackRestContext, 'general', null, {'resourceTypes': 'not-matching-anything'}, function(err, results) {
                                    assert.ok(!err);
                                    assert.equal(results.results.length, 0);

                                    // Verify each single resourceType searches just that one
                                    SearchTestsUtil.searchAll(jackRestContext, 'general', null, {'resourceTypes': 'user'}, function(err, results) {
                                        assert.ok(!err);
                                        _verifyHasResourceTypes(results, true, false, false);

                                        SearchTestsUtil.searchAll(jackRestContext, 'general', null, {'resourceTypes': 'group'}, function(err, results) {
                                            assert.ok(!err);
                                            _verifyHasResourceTypes(results, false, true, false);

                                            SearchTestsUtil.searchAll(jackRestContext, 'general', null, {'resourceTypes': 'content'}, function(err, results) {
                                                assert.ok(!err);
                                                _verifyHasResourceTypes(results, false, false, true);

                                                // Verify searching 2 returns just the 2 types
                                                SearchTestsUtil.searchAll(jackRestContext, 'general', null, {'resourceTypes': ['group', 'content']}, function(err, results) {
                                                    assert.ok(!err);
                                                    _verifyHasResourceTypes(results, false, true, true);

                                                    // Verify searching one with garbage commas returns just the one
                                                    SearchTestsUtil.searchAll(jackRestContext, 'general', null, {'resourceTypes': ['', '', 'content', '']}, function(err, results) {
                                                        assert.ok(!err);
                                                        _verifyHasResourceTypes(results, false, false, true);

                                                        // Verify searching two with garbage commas returns just the two
                                                        SearchTestsUtil.searchAll(jackRestContext, 'general', null, {'resourceTypes': ['', '', 'user', 'content', '', '']}, function(err, results) {
                                                            assert.ok(!err);
                                                            _verifyHasResourceTypes(results, true, false, true);

                                                            // Verify searching with garbage commas and non-matching values still returns by the valid resources types
                                                            SearchTestsUtil.searchAll(jackRestContext, 'general', null, {'resourceTypes': ['', '', 'non-matching', '', 'group', 'user']}, function(err, results) {
                                                                assert.ok(!err);
                                                                _verifyHasResourceTypes(results, true, true, false);
                                                                callback();
                                                            });
                                                        });
                                                    });
                                                });
                                            });
                                        });
                                    });
                                });
                            });
                        });
                    });
                });
            });
        });
    });

    describe('Search Paging', function() {

        /**
         * Test that verifies that the 'start' property properly pages search results
         */
        it('verify search paging', function(callback) {
            var doerUsername = TestsUtil.generateTestUserId('doer');
            RestAPI.User.createUser(camAdminRestContext, doerUsername, 'password', 'Doer', null, function(err, doer) {
                var doerRestContext = TestsUtil.createTenantRestContext(global.oaeTests.tenants.cam.host, doerUsername, 'password');

                // Make sure we have at least 2 content items to page with, their actual information is not important for this test
                RestAPI.Content.createLink(doerRestContext, 'Sakai Foundation', 'Link to Sakai Foundation Website', 'public', 'http://www.sakaifoundation.org', [], [], function(err, link) {
                    assert.ok(!err);

                    RestAPI.Content.createLink(doerRestContext, 'Apereo Foundation', 'Link to Apereo Foundation Website', 'public', 'http://www.apereo.org', [], [], function(err, link) {
                        assert.ok(!err);

                        // Search once and grab the first document id
                        SearchTestsUtil.searchRefreshed(doerRestContext, 'general', null, {'limit': 1}, function(err, results) {
                            assert.ok(!err);
                            assert.ok(results);
                            assert.ok(results.results);
                            assert.equal(results.results.length, 1);

                            var firstDocId = results.results[0].id;
                            assert.ok(firstDocId);

                            // Perform the same search, but with start=0, and make sure the first document is still the same. Verifies default paging
                            RestAPI.Search.search(doerRestContext, 'general', null, {'limit': 1, 'start': 0}, function(err, results) {
                                assert.ok(!err);
                                assert.ok(results);
                                assert.ok(results.results);
                                assert.equal(results.results.length, 1);
                                assert.equal(results.results[0].id, firstDocId);

                                // Search again with start=1 and verify the first document id of the previous search is not the same as the first document id of this search
                                RestAPI.Search.search(doerRestContext, 'general', null, {'limit': 1, 'start': 1}, function(err, results) {
                                    assert.ok(!err);
                                    assert.ok(results);
                                    assert.ok(results.results);
                                    assert.equal(results.results.length, 1);

                                    var secondDocId = results.results[0].id;
                                    assert.ok(secondDocId);

                                    assert.notEqual(firstDocId, secondDocId);
                                    callback();
                                });
                            });
                        });
                    });
                });
            });
        });
    });

