    describe('Search Quality', function() {

        /**
         * Verifies that there is a cut-off point which eliminates documents that are only slightly relevant to the search query.
         * Assuming there are documents which are indexed with:
         *  * OAE Team
         *  * Theological reasoning
         *  * Independent workforce
         * When searching for `Team` only the first document should return.
         */
        it('verify results do not match on a single letter', function(callback) {
            TestsUtil.generateTestUsers(camAdminRestContext, 1, function(err, users) {
                assert.ok(!err);
                var restCtx = _.values(users)[0].restContext;

                // Create some content
                RestAPI.Content.createLink(restCtx, 'Theological reasoning', null, 'public', 'http://www.sakaifoundation.org', [], [], function(err, contentA) {
                    assert.ok(!err);
                    RestAPI.Content.createLink(restCtx, 'Independent workforce', null, 'public', 'http://www.sakaifoundation.org', [], [], function(err, contentB) {
                        assert.ok(!err);
                        var groupAlias = 'OAE Team ' + Math.random();
                        RestAPI.Group.createGroup(restCtx, groupAlias, groupAlias, 'A really awesome group', 'public', 'no', [], [], function(err, group) {
                            assert.ok(!err);

                            // Ensure that only the group returns in the search results when searching for Team
                            SearchTestsUtil.searchAll(restCtx, 'general', null, {'q': 'Team'}, function(err, results) {
                                assert.ok(!err);
                                assert.equal(_.filter(results.results, function(result) { return result.id === contentA.id || result.id === contentB.id; }).length, 0);
                                assert.equal(_.filter(results.results, function(result) { return result.id === group.id; }).length, 1);
                                callback();
                            });
                        });
                    });
                });
            });
        });
    });

    describe('Search Options', function() {

        /**
         * Test that verifies the resourceType parameter in the general search properly filter results by user, group and content.
         */
        it('verify resourceType scope param', function(callback) {
            var jackUsername = TestsUtil.generateTestUserId('jack');
            var groupAlias = TestsUtil.generateTestUserId('group');

            // Ensure a user, group and content item exist in the search index to ensure they are not included in resource-scoped searches
            RestAPI.User.createUser(camAdminRestContext, jackUsername, 'password', 'Jack JickMackerson', null, function(err, jack) {
                assert.ok(!err);
                var jackCtx = TestsUtil.createTenantRestContext(global.oaeTests.tenants.cam.host, jackUsername, 'password');

                RestAPI.Group.createGroup(camAdminRestContext, groupAlias, groupAlias, 'A really awesome group', 'public', 'no', [], [], function(err, group) {
                    assert.ok(!err);

                    RestAPI.Content.createLink(camAdminRestContext, 'Sakai Foundation', 'Link to Sakai Foundation Website', 'public', 'http://www.sakaifoundation.org', [], [], function(err, content) {
                        assert.ok(!err);

                        // Verify we only get users from a user search
                        SearchTestsUtil.searchAll(jackCtx, 'general', null, {'resourceTypes': 'user'}, function(err, results) {
                            assert.ok(!err);
                            assert.ok(_getDocById(results, jack.id));
                            assert.equal(_.filter(results.results, function(result) { return result.resourceType === 'group' }).length, 0);
                            assert.equal(_.filter(results.results, function(result) { return result.resourceType === 'content' }).length, 0);

                            // Verify we only get groups from a group search
                            SearchTestsUtil.searchAll(jackCtx, 'general', null, {'resourceTypes': 'group'}, function(err, results) {
                                assert.ok(!err);
                                assert.ok(_getDocById(results, group.id));
                                assert.equal(_.filter(results.results, function(result) { return result.resourceType === 'user' }).length, 0);
                                assert.equal(_.filter(results.results, function(result) { return result.resourceType === 'content' }).length, 0);

                                // Verify we only get content from a content search
                                SearchTestsUtil.searchAll(jackCtx, 'general', null, {'resourceTypes': 'content'}, function(err, results) {
                                    assert.ok(!err);
                                    assert.ok(_getDocById(results, content.id));
                                    assert.equal(_.filter(results.results, function(result) { return result.resourceType === 'user' }).length, 0);
                                    assert.equal(_.filter(results.results, function(result) { return result.resourceType === 'group' }).length, 0);
                                    callback();
                                });
                            });
                        });
                    });
                });
            });
        });

        /**
         * Test that verifies when includeIndirect is not specified, private resources by indirect access are returned in general search, and when
         * it is disabled, no indirect resources are returned in general search.
         */
        it('verify includeIndirect option', function(callback) {
            var jackUsername = TestsUtil.generateTestUserId('jack');
            var group1Alias = TestsUtil.generateTestUserId('group1');
            var group2Alias = TestsUtil.generateTestUserId('group2');

            // Create a group membership hierarchy: group2 -> group1 -> jack; to test indirect searches
            RestAPI.User.createUser(camAdminRestContext, jackUsername, 'password', 'Jack JickMackerson', null, function(err, jack) {
                assert.ok(!err);
                var jackCtx = TestsUtil.createTenantRestContext(global.oaeTests.tenants.cam.host, jackUsername, 'password');

                RestAPI.Group.createGroup(camAdminRestContext, group1Alias, group1Alias, 'A really awesome group', 'private', 'no', [], [jack.id], function(err, group1) {
                    assert.ok(!err);

                    RestAPI.Group.createGroup(camAdminRestContext, group2Alias, group2Alias, 'A really awesome group', 'private', 'no', [], [group1.id], function(err, group2) {
                        assert.ok(!err);

                        // Search with jack while including indirect, to sanity check that the indirect group is returned
                        SearchTestsUtil.searchAll(jackCtx, 'general', null, {'q': 'awesome'}, function(err, results) {
                            assert.ok(!err);

                            // Verify that using default (includeIndirect === true), we get both groups
                            assert.ok(_getDocById(results, group1.id));
                            assert.ok(_getDocById(results, group2.id));

                            // Search with jack *exluding* indirect groups, verify group2 is not returned
                            SearchTestsUtil.searchAll(jackCtx, 'general', null, {'q': 'awesome', 'includeIndirect': false}, function(err, results) {
                                assert.ok(!err);

                                // Verify that when includeIndirect is disabled, we don't get group2, which is indirectly associated to jack
                                assert.ok(_getDocById(results, group1.id));
                                assert.ok(!_getDocById(results, group2.id));
                                callback();
                            });
                        });
                    });
                });
            });
        });
    });
