var TelemetryAPI = require('oae-telemetry');

 // Initialize the Telemetry API so it starts pushing to redis.
module.exports = function(config, callback) {

    // Make the config object available to the Telemetry API
    TelemetryAPI.setTelemetryConfig(config.telemetry);

    // Grab the correct Publisher.
    if (config.telemetry.enabled && config.telemetry.publisher) {
        var publisher = require('./publishers/' + config.telemetry.publisher);
        publisher.init();
    }

    callback();
};
