var Fields = require('oae-config/lib/fields');

var widths = [
    {
        'name': '25%',
        'value': '3'
    },
    {
        'name': '33%',
        'value': '4'
    },
    {
        'name': '50%',
        'value': '6'
    },
    {
        'name': '66%',
        'value': '8'
    },
    {
        'name': '75%',
        'value': '9'
    },
    {
        'name': '100%',
        'value': '12'
    }
];

/**
 * Create a configurable text block with some configured default values
 *
 * @param  {String}     [type]                  The block type. Defaults to `empty`
 * @param  {Number}     [xs]                    Block width at extra small resolution. Defaults to 12
 * @param  {Number}     [sm]                    Block width at small resolution. Defaults to 12
 * @param  {Number}     [md]                    Block width at medium resolution. Defaults to 12
 * @param  {Number}     [lg]                    Block width at large resolution. Defaults to 12
 * @param  {Number}     [minHeight]             The minimum heigh in pixels
 * @param  {String}     [bgColor]               The default background color. If left null, the block will be transparent
 * @param  {String}     [titleColor]            The default title color
 * @param  {String}     [textColor]             The default text color
 * @param  {String}     [text]                  The default text
 * @param  {String}     [icon]                  The default icon
 * @param  {String}     [imgUrl]                The default image URL
 * @param  {String}     [videoUrl]              The default video URL
 * @param  {String}     [videoPlaceholder]      The default video placeholder image
 * @return {Object}                             A configurable text block
 * @api private
 */
var _createBlock = function(type, xs, sm, md, lg, minHeight, bgColor, titleColor, textColor, text, icon, imgUrl, videoUrl, videoPlaceholder) {
    type = type || 'empty';
    xs = xs || 12;
    sm = sm || 12;
    md = md || 12;
    lg = lg || 12;

    return {
        'name': 'Block values',
        'description': 'Block values',
        'elements': {
            'type': new Fields.List('Block type', 'Block type', type, [
                // TODO: Remove `empty` type once a better admin UI is in place
                {
                    'name': 'Empty',
                    'value': 'empty'
                },
                {
                    'name': 'Search',
                    'value': 'search'
                },
                {
                    'name': 'Text',
                    'value': 'text'
                },
                {
                    'name': 'Text with icon',
                    'value': 'iconText'
                },
                {
                    'name': 'Image',
                    'value': 'image'
                },
                {
                    'name': 'Video',
                    'value': 'video'
                }
            ],  {'suppress': true}),
            'xs': new Fields.List('XS Block width', 'Block width at extra small resolution', xs, widths, {'suppress': true}),
            'sm': new Fields.List('SM Block width', 'Block width at small resolution', sm, widths, {'suppress': true}),
            'md': new Fields.List('MD Block width', 'Block width at medium resolution', md, widths, {'suppress': true}),
            'lg': new Fields.List('LG Block width', 'Block width at large resolution', lg, widths, {'suppress': true}),
            'minHeight': new Fields.Text('Block minimum height', 'Minimum height for the block in px', minHeight, {'suppress': true}),
            'bgColor': new Fields.Text('Block background color', 'Background color for the block', bgColor, {'suppress': true}),
            'titleColor': new Fields.Text('Block title color', 'Title color for the block', titleColor, {'suppress': true}),
            'textColor': new Fields.Text('Block text color', 'Text color for the block', textColor, {'suppress': true}),
            'text': new Fields.InternationalizableText('Block text', 'Text content for the block', text, {'suppress': true}),
            'icon': new Fields.Text('Block icon', 'Icon for the block', icon, {'suppress': true}),
            'imgUrl': new Fields.Text('Image URL', 'Image URL', imgUrl, {'suppress': true}),
            'videoUrl': new Fields.Text('Video URL', 'Video URL', videoUrl, {'suppress': true}),
            'videoPlaceholder': new Fields.Text('Video Placeholder Image', 'URL for video placeholder image', videoPlaceholder, {'suppress': true})
        }
    };
};

module.exports = {
    'title': 'OAE Tenant Module',
    'block_1': _createBlock('search', 12, 12, 12, 12),
    'block_2': _createBlock('video', 12, 12, 8, 8, 290, null, null, null, null, null, null, '//www.youtube.com/embed/cfiM87Y0pWw?rel=0&autoplay=1&showinfo=0&modestbranding=1', '/ui/img/index-video-bg.png'),
    'block_3': _createBlock('text', 12, 6, 4, 4, null, null, '#fff', '#fff', '# __MSG__SUPPORTING_ACADEMIC_COLLABORATION__\n#####__MSG__A_POWERFULL_NEW_WAY_FOR_STUDENTS_AND_FACULTY_TO_CREATE_KNOWLEDGE_COLLABORATE_AND_CONNECT_WITH_THE_WORLD__'),
    'block_4': _createBlock('iconText', 12, 6, 4, 4, null, '#fff', '#4199ca', '#000', '#### __MSG__AUTHORING_EXPERIENCE__\n__MSG__RICH_COMPELLING_INTERACTIVE_CONTENT_AUTHORING__', 'fa-edit'),
    'block_5': _createBlock('iconText', 12, 6, 4, 4, null, '#424242', '#fff', '#fff', '#### __MSG__CHANNELS_OF_COMMUNICATION__\n__MSG__PARTICIPATING_IN_DISCUSSIONS_AND_FEEDBACK_WITHIN_PERSONALIZED_NETWORKS__', 'fa-comments'),
    'block_6': _createBlock('iconText', 12, 6, 4, 4, null, '#f0eeec', '#424242', '#000', '#### __MSG__ACCESS_TO_CONTENT__\n__MSG__EXPANDED_ACCESS_TO_LEARNING_AND_RESEARCH_MATERIALS_BETTER_CONNECTS_LIBRARY_SERVICES__','fa-cloud-download'),
    'block_7': _createBlock(),
    'block_8': _createBlock(),
    'block_9': _createBlock(),
    'block_10': _createBlock(),
    'block_11': _createBlock(),
    'block_12': _createBlock()
};
