var Cassandra = require('oae-util/lib/cassandra');
var TenantAPI = require('oae-tenants');

module.exports = function(app, callback) {

    registerGlobalRestEndpoints(app);

    // create the schema and register / start active tenants
    ensureSchema(function(err) {
        if (err) {
            return callback(err);
        }

        TenantAPI.getAllTenants(function(err, tenants) {
            if (err) {
                return callback(err);
            }

            for (var t = 0; t < tenants.length; t++) {
                TenantAPI.startTenant(tenants[t]);
            }

            callback();
        });
    });
}


/**
 * Ensure that the tenant schema is created. If the tenant schema has not been created, or the default tenant has not been seeded,
 * both will be performed automatically. If both the schema and the default tenant exist, then this method will do nothing.
 * 
 * @param {Function} callback A function(err), specifying whether or not there was an error creating the schema.
 */
var ensureSchema = function(callback) {
    Cassandra.createColumnFamily('Tenant', 'CREATE COLUMNFAMILY Tenant (port text PRIMARY KEY, alias text, name text, baseUrl text, active boolean, deleted boolean)', callback);
};

/**
 * Register the global tenant end-points for interacting with tenants.
 */
var registerGlobalRestEndpoints = function(app) {

    app.server.post('/api/tenant', function(req, res) {
        // TODO: Secure this + unit test security
        TenantAPI.updateTenant(req.body.port, req.body.name, function(err) {
            if (err) {
                return res.send(err.code, err.msg);
            }
            res.send(200);
        });
    });

    app.server.post('/api/tenant/create', function(req, res) {
        // TODO: Secure this + unit test security
        TenantAPI.createTenant(req.body.id, req.body.name, req.body.port, req.body.baseurl, function(err) {
            if (err) {
                return res.send(err.code, err.msg);
            }
            res.send(200, 'New tenant "' + req.body.name + '" has been fired up on port ' + req.body.port);
        });
    });

    app.server.post('/api/tenant/start', function(req, res) {
        // TODO: Secure this + unit test security
        // sets the tenant to be ENABLED, by passing in 'false'
        TenantAPI.disableTenants(req.body.tenants, false, function(err) {
            if (err) {
                return res.send(err.code, err.msg);
            }
            res.send(200);
        });
    });

    app.server.post('/api/tenant/stop', function(req, res) {
        // TODO: Secure this + unit test security
        // sets the tenant to be disabled, by passing in 'true'
        TenantAPI.disableTenants(req.body.tenants, true, function(err) {
            if (err) {
                return res.send(err.code, err.msg);
            }
            res.send(200);
        });
    });

    app.server.post('/api/tenant/delete', function(req, res) {
        // TODO: Secure this + unit test security
        TenantAPI.deleteTenants(req.body.tenants, function(err) {
            if (err) {
                return res.send(err.code, err.msg);
            }
            res.send(200);
        });
    });

    app.server.get('/api/tenants', function(req, res, next) {
        TenantAPI.getAllTenants(function(err, tenants) {
            if (err) {
                return res.send(err.code, err.msg);
            }
            res.send(200, tenants);
        });
    });

}
