/**
 * The Tenant object represents a tenant in the OAE data-store.
 * 
 * @param {String} alias The assigned unique alias of the tenant
 * @param {String} name A descriptive short name for the tenant
 * @param {Number} port The port on which the tenant listens
 * @param {String} baseUrl The baseUrl on which this tenant is proxying (ie: oae.cam.ac.uk or oae.gatech.edu)
 */
var Tenant = module.exports.Tenant = function(alias, name, port, baseUrl, active, deleted) {
    var that = {};

    that.alias = alias;
    that.name = name;
    that.baseUrl = baseUrl;
    that.port = isNaN(port) ? parseInt(port, 10) : port;
    that.active = active;
    that.deleted = deleted;

    return that;
};

module.exports.SystemTenant = new module.exports.Tenant('system', 'System tenant', 2000, 'localhost');
