/**
 * The Tenant object represents a tenant in the OAE data-store.
 *
 * @param  {String}     alias           The assigned unique alias of the tenant
 * @param  {String}     displayName     A descriptive short name for the tenant
 * @param  {String}     host            The host on which this tenant is proxying (ie: oae.cam.ac.uk or oae.gatech.edu)
 * @param  {Boolean}    active          Whether or not the tenant is active
 * @param  {Boolean}    deleted         Whether or not the tenant is deleted
 */
var Tenant = module.exports.Tenant = function(alias, displayName, host, active, deleted) {
    var that = {};

    that.alias = alias;
    that.displayName = displayName;
    that.host = host;
    that.active = active;
    that.deleted = deleted;

    /**
     * Creates and returns a compact representation of the Tenant object.
     *
     * @return {Object} A compact representation of the Tenant object.
     */
    that.compact = function() {
        return {
            'alias': that.alias,
            'displayName': that.displayName
        };
    };

    return that;
};
