/**
 * The Tenant object represents a tenant in the OAE data-store.
 * 
 * @param {String} alias The assigned unique alias of the tenant
 * @param {String} name A descriptive short name for the tenant
 * @param {String} description A long description for the tenant
 * @param {Number} port The port on which the tenant listens
 */
module.exports.Tenant = function(alias, name, description, port) {
    var that = {};

    that.alias = alias;
    that.name = name;
    that.description = description;
    that.port = isNaN(port) ? parseInt(port, 10) : port;

    return that;
};