var _ = require('underscore');
var OAE = require('oae-util/lib/oae');

var TenantsAPI = require('oae-tenants/lib/api');
var TenantNetworksAPI = require('oae-tenants/lib/api.networks');


/////////////////////
// TENANT NETWORKS //
/////////////////////

/**
 * @REST getTenantNetworks
 *
 * Get all tenant networks and their associated tenants
 *
 * @Server      admin
 * @Method      GET
 * @Path        /tenantNetworks
 * @Return      {TenantNetworks}                All available tenant networks
 */
OAE.globalAdminRouter.on('get', '/api/tenantNetworks', function(req, res) {
    TenantNetworksAPI.getTenantNetworks(req.ctx, function(err, tenantNetworks) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        return res.send(200, tenantNetworks);
    });
});

/**
 * @REST postTenantNetworkCreate
 *
 * Create a tenant network
 *
 * @Server      admin
 * @Method      POST
 * @Path        /tenantNetwork/create
 * @FormParam   {string}                displayName     The display name of the tenant network
 * @Return      {TenantNetwork}                         The tenant network that was created
 */
OAE.globalAdminRouter.on('post', '/api/tenantNetwork/create', function(req, res) {
    TenantNetworksAPI.createTenantNetwork(req.ctx, req.body.displayName, function(err, tenantNetwork) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        return res.send(201, tenantNetwork);
    });
});

/**
 * @REST postTenantNetwork
 *
 * Update a tenant network
 *
 * @Server      admin
 * @Method      POST
 * @Path        /tenantNetwork/{id}
 * @PathParam   {string}                id              The id of the tenant network to update
 * @FormParam   {string}                displayName     The new display name of the tenant network
 * @Return      {TenantNetwork}                         The updated tenant network
 */
OAE.globalAdminRouter.on('post', '/api/tenantNetwork/:id', function(req, res) {
    TenantNetworksAPI.updateTenantNetwork(req.ctx, req.params.id, req.body.displayName, function(err, tenantNetwork) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        return res.send(200, tenantNetwork);
    });
});

/**
 * @REST deleteTenantNetworkId
 *
 * Delete a tenant network
 *
 * @Server      admin
 * @Method      DELETE
 * @Path        /tenantNetwork/{id}
 * @PathParam   {string}                id      The id of the tenant network to delete
 */
OAE.globalAdminRouter.on('delete', '/api/tenantNetwork/:id', function(req, res) {
    TenantNetworksAPI.deleteTenantNetwork(req.ctx, req.params.id, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        return res.send(200);
    });
});

/**
 * @REST postTenantNetworkIdAddTenants
 *
 * Add the provided tenant(s) to a tenant network
 *
 * @Server      admin
 * @Method      POST
 * @Path        /tenantNetwork/{id}/addTenants
 * @PathParam   {string}                    id          The id of the tenant network to which to add the provided tenant aliases
 * @FormParam   {string[]}                  alias       The tenant alias(es) to add to the tenant network
 * @Return      {void}
 */
OAE.globalAdminRouter.on('post', '/api/tenantNetwork/:id/addTenants', function(req, res) {
    var tenantAliases = _.isString(req.body.alias) ? [req.body.alias] : req.body.alias;
    TenantNetworksAPI.addTenantAliases(req.ctx, req.params.id, tenantAliases, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        return res.send(200);
    });
});

/**
 * @REST postTenantNetworkIdRemoveTenants
 *
 * Remove the provided tenant(s) from a tenant network
 *
 * @Server      admin
 * @Method      POST
 * @Path        /tenantNetwork/{id}/removeTenants
 * @PathParam   {string}                    id          The id of the tenant network from which to remove the provided tenant aliases
 * @FormParam   {string[]}                  alias       TThe tenant alias(es) to remove from the tenant network
 * @Return      {void}
 */
OAE.globalAdminRouter.on('post', '/api/tenantNetwork/:id/removeTenants', function(req, res) {
    var tenantAliases = _.isString(req.body.alias) ? [req.body.alias] : req.body.alias;
    TenantNetworksAPI.removeTenantAliases(req.ctx, req.params.id, tenantAliases, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        return res.send(200);
    });
});


/////////////
// TENANTS //
/////////////

/**
 * @REST postTenantCreate
 *
 * Create a new tenant
 *
 * @Server      admin
 * @Method      POST
 * @Path        /tenant/create
 * @FormParam   {string}            alias           The unique alias for the tenant
 * @FormParam   {string}            displayName     A descriptive short name for the tenant
 * @FormParam   {string}            host            The host on which this tenant will be proxying
 * @Return      {Tenant}                            The created tenant
 */
OAE.globalAdminRouter.on('post', '/api/tenant/create', function(req, res) {
    TenantsAPI.createTenant(req.ctx, req.body.alias, req.body.displayName, req.body.host, function(err, tenant) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, tenant);
    });
});

/**
 * @REST getTenants
 *
 * Get a list of all available tenants
 *
 * @Server      admin
 * @Method      GET
 * @Path        /tenants
 * @Return      {Tenants}                       All available tenants
 */
OAE.globalAdminRouter.on('get', '/api/tenants', function(req, res, next) {
    var tenants = TenantsAPI.getTenants();
    res.send(200, tenants);
});

/**
 * @REST getTenant
 *
 * Get the current tenant
 *
 * @Server      admin,tenant
 * @Method      GET
 * @Path        /tenant
 * @Return      {Tenant}                        The current tenant
 */
var _getCurrentTenant = function(req, res) {
    res.send(200, req.ctx.tenant());
};

OAE.globalAdminRouter.on('get', '/api/tenant', _getCurrentTenant);
OAE.tenantRouter.on('get', '/api/tenant', _getCurrentTenant);

/**
 * @REST getTenantAlias
 *
 * Get a tenant by alias
 *
 * @Server      admin
 * @Method      GET
 * @Path        /tenant/{alias}
 * @PathParam   {string}           alias        Alias for the tenant that should be retrieved
 * @Return      {Tenant}                        The requested tenant
 */
OAE.globalAdminRouter.on('get', '/api/tenant/:alias', function(req, res) {
    var tenant = TenantsAPI.getTenant(req.params.alias);
    if (!tenant) {
        return res.send(404, 'There is no tenant with alias ' + req.params.alias);
    }
    res.send(200, tenant);
});

/**
 * @REST postTenant
 *
 * Update the current tenant
 *
 * @Server      tenant
 * @Method      POST
 * @Path        /tenant
 * @FormParam   {string}            [displayName]   Updated tenant display name
 * @FormParam   {string}            [host]          Updated tenant host name
 * @Return      {void}
 */
OAE.tenantRouter.on('post', '/api/tenant', function(req, res) {
    TenantsAPI.updateTenant(req.ctx, req.ctx.tenant().alias, req.body, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200);
    });
});

/**
 * @REST postTenantStart
 *
 * Start a tenant
 *
 * @Server      admin
 * @Method      POST
 * @Path        /tenant/start
 * @FormParam   {string[]}          aliases         A tenant alias or array of tenant aliases representing the tenants that should be started
 * @Return      {void}
 */
OAE.globalAdminRouter.on('post', '/api/tenant/start', function(req, res) {
    // Sets the tenant to be ENABLED by passing in 'false'
    TenantsAPI.disableTenants(req.ctx, req.body.aliases, false, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200);
    });
});

/**
 * @REST postTenantStop
 *
 * Stop a tenant
 *
 * @Server      admin
 * @Method      POST
 * @Path        /tenant/stop
 * @FormParam   {string[]}          aliases         A tenant alias or array of tenant aliases representing the tenants that should be stopped
 * @Return      {void}
 */
OAE.globalAdminRouter.on('post', '/api/tenant/stop', function(req, res) {
    // Sets the tenant to be disabled by passing in 'true'
    TenantsAPI.disableTenants(req.ctx, req.body.aliases, true, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200);
    });
});

/**
 * @REST postTenantAlias
 *
 * Update a tenant's metadata
 *
 * @Server      admin
 * @Method      POST
 * @Path        /tenant/{alias}
 * @PathParam   {string}            alias           The alias of the tenant to update
 * @FormParam   {string}            [displayName]   Updated tenant display name
 * @FormParam   {string}            [host]          Updated tenant host name
 * @Return      {void}
 */
OAE.globalAdminRouter.on('post', '/api/tenant/:alias', function(req, res) {
    TenantsAPI.updateTenant(req.ctx, req.params.alias, req.body, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200);
    });
});
