var express = require('express');
var fs = require('fs');

var configAPI = require('oae-config-aggregator');
var tenantAPI = require('oae-tenants');

module.exports = function(tenant) {

    tenant.server.use(express.static(__dirname + "/../../oae-config-aggregator/public"));

    tenant.server.get('/api/tenant', function(req, res) {
        res.send({
            'alias': tenant.alias,
            'name': tenant.name,
            'description': tenant.description,
            'port': tenant.port
        });
    });

    tenant.server.get('/admin/tenant/*', function(req, res, next) {
        var action = req.pathname;
        fs.readFile('node_modules/oae-config-aggregator/public/admin.html', 'binary', function(err, file) {
            if (err) {
                return res.send(err.code, err.msg);
            }
            res.send(200, {'Content-Type': 'text/html'});
        });
    });

    tenant.server.get('/api/configuration', function(req, res, next) {
        configAPI.readConfig(req.query.tenantid, function(err, configuration) {
            if (err) {
                return res.send(err.code, err.msg);
            }
            res.send(200, configuration);
        });
    });

    tenant.server.post('/admin/update', function(req, res, next) {
        configAPI.writeConfig(req.body, function(err) {
            if (err) {
                return res.send(err.code, err.msg);
            }
            res.send(200, 'Config successfully stored.');
        });
    });

};