var express = require('express');
var fs = require('fs');

var Context = require('oae-context').Context;
var log = require('oae-logger').logger('config');
var OAE = require('oae-util/lib/oae');

var configAPI = require('oae-config');
var TenantAPI = require('oae-tenants');

module.exports = function(tenant) {

    tenant.server.get('/api/tenant', function(req, res) {
        TenantAPI.getTenantByAlias(tenant.alias, function(err, tenant) {
            if (err) {
                return res.send(err.code, err.msg);
            }
            res.send(200, tenant);
        });
    });

    tenant.server.post('/api/tenant', function(req, res) {
        if (!req.user || !req.user.isAdmin(tenant.alias)) {
            return res.send(401, 'Unauthorized');
        }

        TenantAPI.updateTenant(req.body.port, req.body.name, function(err) {
            if (err) {
                return res.send(err.code, err.msg);
            }
            res.send(200);
        });
    });

    tenant.server.get('/api/tenants', function(req, res, next) {
        TenantAPI.getAllTenants(function(err, tenants) {
            if (err) {
                return res.send(err.code, err.msg);
            }
            res.send(200, tenants);
        });
    });

    tenant.server.get('/admin', function(req, res, next) {
        fs.readFile('node_modules/oae-config/public/admin.html', 'binary', function(err, file) {
            if (err) {
                res.writeHead(500, {'Content-Type': 'text/plain'});
                res.write("Page not found.");
                res.end();
                return;
            }

            res.writeHead(200, {'Content-Type': 'text/html'});
            res.write(file, 'binary');
            res.end();
        });
    });
};
