var assert = require('assert');

var Cassandra = require('oae-util/lib/cassandra');
var api = require('oae-tenants/lib/api');
var schema = require('oae-tenants/lib/schema');

describe('Tenants', function() {

    it('should be able to access a tenant by their mapped port', function(callback) {
        api.getTenantByPort(2001, function(err, tenant) {
            assert.ok(!err);
            assert.equal(2001, tenant.port);
            assert.equal('camtest', tenant.alias);
            assert.equal('Cambridge University Test', tenant.name);
            assert.equal('Cambridge University Description', tenant.description);
            assert.equal('oae.cam.ac.uk', tenant.baseUrl);
            api.getTenantByPort(2002, function(err, tenant) {
                assert.ok(!err);
                assert.equal(2002, tenant.port);
                assert.equal('gttest', tenant.alias);
                assert.equal('Georgia Tech Test', tenant.name);
                assert.equal('Georgia Tech Description', tenant.description);
                assert.equal('oae.gatech.edu', tenant.baseUrl);
                callback();
            });
        });

    });

    it('should be able to access a tenant by their mapped alias', function(callback) {
        api.getTenantByAlias('camtest', function(err, tenant) {
            assert.ok(!err);
            assert.equal(2001, tenant.port);
            assert.equal('camtest', tenant.alias);
            assert.equal('Cambridge University Test', tenant.name);
            assert.equal('Cambridge University Description', tenant.description);
            assert.equal('oae.cam.ac.uk', tenant.baseUrl);
            api.getTenantByAlias('gttest', function(err, tenant) {
                assert.ok(!err);
                assert.equal(2002, tenant.port);
                assert.equal('gttest', tenant.alias);
                assert.equal('Georgia Tech Test', tenant.name);
                assert.equal('Georgia Tech Description', tenant.description);
                assert.equal('oae.gatech.edu', tenant.baseUrl);
                callback();
            });
        });
    });

    it('should be able to create a tenant', function(callback) {
        api.createTenant('rsmarttest', 'rSmart Test', 'rSmart Description', 2003, "oae.rsmart.com", function(err, tenant) {
            assert.ok(!err);
            assert.equal('rsmarttest', tenant.alias);
            assert.equal('rSmart Test', tenant.name);
            assert.equal('rSmart Description', tenant.description);
            assert.equal('oae.rsmart.com', tenant.baseUrl);
            assert.equal(2003, tenant.port);
            api.getTenantByPort(2003, function(err, tenant) {
                assert.ok(tenant);
                assert.equal('rsmarttest', tenant.alias);
                assert.equal('rSmart Test', tenant.name);
                assert.equal('rSmart Description', tenant.description);
                assert.equal('oae.rsmart.com', tenant.baseUrl);
                assert.equal(2003, tenant.port);
                callback();
            });
        });
    });

    it('should not be able to create a tenant with a duplicate port', function(callback) {
        api.createTenant('testCannotCreateDuplicatePort', 'Test Tenant', 'A tenant for testing', 2001, "duplicate.com", function(err, tenant) {
            assert.ok(err);
            api.getTenantByPort(2001, function(err, tenant) {
                assert.ok(!err);
                assert.equal(2001, tenant.port);
                assert.equal('camtest', tenant.alias);
                assert.equal('Cambridge University Test', tenant.name);
                assert.equal('Cambridge University Description', tenant.description);
                assert.equal('oae.cam.ac.uk', tenant.baseUrl);
                callback();
            });
        });
    });

});