var _ = require('underscore');

var RestAPI = require('oae-rest');
var RestContext = require('oae-rest/lib/model').RestContext;

/**
 * Generate a number of random users that can be used inside of tests
 * @param {RestContext}                     restCtx             Standard REST Context object that contains the current tenant URL and the current
 *                                                              user credentials
 * @param {Number}                          total               The total number of test groups that need to be created. If not provided, a single
 *                                                              test group will be created
 * @param {Function(err, createdUsers)}     callback            Standard callback method
 * @param {Object}                          callback.err        Error object containing error code and error message
 * @param {Object}                          callback.response   Object where the keys are the user ids of the created users and the values are
 *                                                              the actual user objects
 */
var generateTestUsers = module.exports.generateTestUsers = function(restCtx, total, callback) {
    total = total || 1;
    var createdUsers = {};

    var userCreated = function(err, userObj) {
        if (err) {
            return callback({'code': 500, 'msg': 'An error occured whilst creating random test users'});
        }
        createdUsers[userObj.id] = userObj;
        if (_.keys(createdUsers).length === total) {
            callback(false, createdUsers);
        }
    };
    
    for (var u = 0; u < total; u++) {
        RestAPI.User.createUser(getAdminRestContext(restCtx.baseUrl), generateTestUserId('random-user'), 'password', 'public', 'en_GB', 'Europe/London', 'John', 'Doe', 'John Doe', userCreated);
    }
};

/**
 * Generate a number of random groups that can be used inside of tests
 * @param {RestContext}                     restCtx             Standard REST Context object that contains the current tenant URL and the current
 *                                                              user credentials
 * @param {Number}                          total               The total number of test groups that need to be created. If not provided, a single
 *                                                              test group will be created
 * @param {Function(err, createdGroups)}    callback            Standard callback method
 * @param {Object}                          callback.err        Error object containing error code and error message
 * @param {Object}                          callback.response   Object where the keys are the group ids of the created groups and the values are
 *                                                              the actual group objects
 */
var generateTestGroups = module.exports.generateTestGroups = function(restCtx, total, callback) {
    total = total || 1;
    var createdGroups = {};

    var groupCreated = function(err, groupObj) {
        if (err) {
            return callback({'code': 500, 'msg': 'An error occured whilst creating random test groups'});
        }
        createdGroups[groupObj.id] = groupObj;
        if (_.keys(createdGroups).length === total) {
            callback(false, createdGroups);
        }
    };
    
    // We first have to make sure that the user creating the groups is logged in, otherwise the asynchronous
    // nature will confuse the cookie jar management in OAE Rest
    RestAPI.User.getMe(restCtx, function(err, meObj) {
        if (err) {
            return callback({'code': 500, 'msg': 'An error occured whilst trying to log in the user'});
        }
        for (var g = 0; g < total; g++) {
            RestAPI.Group.createGroup(restCtx, generateTestGroupId('random-group'),'Group title', 'Group description', 'public', 'yes', [], [], groupCreated);
        }
    });
};

/**
 * Generate a random user id that can be used inside of tests. We use a double Math.random randomizer to make
 * the collision chances even smaller.
 * @param  {String}     seed        String that should be used as the first part of the generated user id
 * @return {String}                 Random user id
 */
var generateTestUserId = module.exports.generateTestUserId = function(seed) {
    return (seed || 'user') + '-' + Math.floor(Math.random() * 1000000) + '-' + Math.floor(Math.random() * 1000000);
};

/**
 * Generate a random group id that can be used inside of tests. We use a double Math.random randomizer to make
 * the collision chances even smaller.
 * @param  {String}     seed        String that should be used as the first part of the generated group id
 * @return {String}                 Random group id
 */
var generateTestGroupId = module.exports.generateTestGroupId = function(seed) {
    return (seed || 'group') + '-' + Math.floor(Math.random() * 1000000) + '-' + Math.floor(Math.random() * 1000000);
};

/**
 * Create a Rest Context object that represents an anonymous user and can be created for tests
 * @param  {String}         baseUrl             Tenant URL for the tenant on which we want to perform a REST call
 * @return {RestContext}                        Rest Context object that represents an anonymous user on the provided
 *                                              tenant
 */
var getAnonymousRestContext = module.exports.getAnonymousRestContext = function(baseUrl) {
    return new RestContext(baseUrl, null, null);
};

/**
 * Create a Rest Context object that represents the global admin user and can be created for tests
 * @param  {String}         baseUrl             Tenant URL for the tenant on which we want to perform a REST call
 * @return {RestContext}                        Rest Context object that represents the global admin user on the provided
 *                                              tenant
 */
var getAdminRestContext = module.exports.getAdminRestContext = function(baseUrl) {
    return new RestContext(baseUrl, 'administrator', 'administrator');
};