/**
 * An object that is similar to the Context object in oae-context.
 * The main difference is that this is only used to contact the REST apis.
 * @param {String}  baseUrl     The base url where an OAE tenant is running.
 *                              ex: localhost:2001 or cam.oae-performance.sakaiproject.org
 * @param {Stirng}  userId      A userId.
 *                              ex: u:cam:simong
 * @param {String}  password    A password that can be used to log this user in.
 */
module.exports = function(baseUrl, userId, password) {
    var that = {};
    that.baseUrl = baseUrl;
    that.user = {};
    that.user.id = userId;
    that.user.password = password;
    that.user.username = (userId !== null) ? userId.split(':')[2] : null;
    return that;
};