var cassandra = require('oae-util/lib/cassandra');
var log = require('oae-logger').logger('oae-transfer');

module.exports = function(config, callback) {
    log().info('Initializing the oae-transfer module');
    _ensureSchema(callback);
};

/**
 * Ensure that the all of the transfer schemas are created. If they already exist, this method will not do anything
 *
 * @param  {Function}         callback       Standard callback function
 * @param  {Object}           callback.err   An error that occurred
 * @api private
 */
var _ensureSchema = function(callback) {
    cassandra.createColumnFamilies({
        'TransferById': 'CREATE TABLE "TransferById" ("originalUserId" text PRIMARY KEY, "code" text, "originalEmail" text, "targetEmail" text)',
        'TransferByEmail': 'CREATE TABLE "TransferByEmail" ("originalEmail" text, "code" text, "originalUserId" text, "targetEmail" text, PRIMARY KEY ("originalEmail", "code"))',
        'RecordedTransfer': 'CREATE TABLE "RecordedTransfer" ("originalEmail" text, "originalUserId" text, "targetEmail" text, "targetUserId" text, PRIMARY KEY ("originalUserId"))'
    }, callback);
};
