/**
 * A model object that represents a transfer
 *
 * @param  {String}     originalEmail     The email of the account which will be removed
 * @param  {String}     code            The code used by the user to secure the transfer
 * @param  {String}     targetEmail     The email of the account which will receive all the data
 * @param  {String}     originalUserId    The ID of the account which will be removed
 */
var TransferById = module.exports.TransferById = function (originalUserId, code, originalEmail, targetEmail) {
    var that = {};
    that.originalUserId = originalUserId;
    that.code = code;
    that.originalEmail = originalEmail;    
    that.targetEmail = targetEmail;
    return that;
};

