/**
 * A model object that represents a transfer
 *
 * @param  {String}     originalEmail       The email of the account from which the data will be transferred
 * @param  {String}     code                The code used by the user to secure the transfer
 * @param  {String}     targetEmail         The email of the account to which the data will be transferred
 * @param  {String}     originalUserId      The account identifier from which the data will be transferred
 * @param  {String}     targetUserId        The identifier of the account to which the data will be transferred
 * @param  {String}     status              The status of the transfer. This attribut can take the value of 'completed', 'ongoing' or 'canceled'
 * @param  {Date}       dateLastAction      The date of the last action
 */

let Transfer = module.exports.Transfer = function (originalUserId, code, originalEmail, targetEmail, targetUserId, status, dateLastAction) {
    var that = {};
    that.originalUserId = originalUserId;
    that.code = code;
    that.originalEmail = originalEmail;    
    that.targetEmail = targetEmail;
    that.targetUserId = targetUserId;
    that.status = status;
    that.dateLastAction = dateLastAction;
    return that;
};
