var OAE = require('oae-util/lib/oae');
var TransferAPI = require('oae-transfer');

/**
 * @REST postTransferCreate
 *
 * Create a new transfer
 *
 * @Server      tenant
 * @Method      POST
 * @Path        /transfer/create
 * @FormParam   {Context}           ctx                 Context
 * @FormParam   {string}            originalEmail         The email of the user who create the transfer   
 * @FormParam   {string}            targetEmail         The new email       		
 * @FormParam   {string}            originalUserId        The id of the user who create the transfer               
 * @Return      {transfer}                              The created transfer
 * @HttpResponse                    200                 Transfer created
 * @HttpResponse                    400                 targetEmail cannot be empty
 * @HttpResponse                    400                 Transfer not found
 */
OAE.tenantRouter.on('post', '/api/transfer/create', function (req, res) {
    TransferAPI.createTransfer(req.ctx, req.body.originalUserId, req.body.originalEmail, req.body.targetEmail, function (err, transfer) {
        if (err) {
            return res.status(err.code).send(err.msg);
        }
        return res.status(200).send(transfer);
    });
});

/**
 * @REST getTransferByID
 *
 * Get transfer
 *
 * @Server      tenant
 * @Method      GET
 * @Path        /transfer/getTransferById/:originalUserId
 * @FormParam   {Context}           ctx                 Context
 * @FormParam   {string}            originalUserId        The id of the user who created the transfer               
 * @Return      {transfer}                              The created transfer
 * @HttpResponse                    200                 Transfer created
 * @HttpResponse                    400                 Transfer not found
 */
OAE.tenantRouter.on('get', '/api/transfer/getTransferById/:originalUserId', function (req, res) {
    TransferAPI.getTransferById(req.ctx, req.params.originalUserId, function (err, transfer) {
        if (err) {
            return res.status(err.code).send(err.msg);
        }
        return res.status(200).send(transfer);
    });
});

/**
 * @REST makeTransfer
 *
 * Make transfer
 *
 * @Server      tenant
 * @Method      POST
 * @Path        /transfer/makeTransfer
 * @FormParam   {Context}           ctx                 Context
 * @FormParam   {string}            originalEmail         The email
 * @FormParam   {string}            code                Generated code
 * @FormParam   {string}            targetEmail         The new email               
 * @FormParam   {string}            targetUserId        The ID user                           
 * @Return      {transfer}                              The created transfer
 * @HttpResponse                    200                 Transfer created
 * @HttpResponse                    400                 Email cannot be empty
 * @HttpResponse                    400                 Code cannot be empty
 * @HttpResponse                    400                 Invalid email
 * @HttpResponse                    400                 Transfer not found
 * @HttpResponse                    400                 There is no data to transfer
 * @HttpResponse                    400                 Code doesn't match
 * @HttpResponse                    400                 Transfer not found
 */
OAE.tenantRouter.on('post', '/api/transfer/makeTransfer', function (req, res) {
    TransferAPI.makeTransfer(req.ctx, req.body.originalEmail, req.body.code, req.body.targetEmail, req.body.targetUserId, function (err, managers) {
        if (err) {
            return res.status(err.code).send(err.msg);
        }
        return res.status(200).send(managers);
    });
});

/**
 * @REST deleteTransfer
 *
 * Delete transfer
 *
 * @Server      tenant
 * @Method      POST
 * @Path        /transfer/deleteTransfer
 * @FormParam   {Context}           ctx                 Context
 * @FormParam   {string}            originalEmail         The email           
 * @FormParam   {string}            code                Generated code
 * @FormParam   {string}            originalUserId        The id of the user who created the transfer  
 * @Return      {transfer}                              The created transfer
 * @HttpResponse                    200                 Transfer created
 * @HttpResponse                    400                 Email cannot be empty
 * @HttpResponse                    400                 Invalid email
 */
OAE.tenantRouter.on('post', '/api/transfer/deleteTransfer', function (req, res) {
    TransferAPI.deleteTransfer(req.ctx, req.body.originalEmail, req.body.code, req.body.originalUserId, function (err) {
        if (err) {
            return res.status(err.code).send(err.msg);
        }
        return res.status(200).send();
    });
});
