var _ = require('underscore');
var assert = require('assert');
var fs = require('fs');
var util = require('util');

var AuthzAPI = require('oae-authz');
var RestAPI = require('oae-rest');

var TestsUtil = require('oae-tests');
var TransferConstants = require('oae-transfer/lib/constants').TransferConstants;
var TransferTestUtil = require('oae-transfer/lib/test/util');

describe('Transfer', function() {

    var globalAdminRestContext = null;
    var camAdminRestContext = null;
    var camAnonymousRestContext = null;
    var gtAdminRestContext = null;
    var gtAnonymousRestContext = null;

    /*
     * Set up all the REST contexts for admin and anonymous users with which we
     * will invoke requests
     */
    before(function(callback) {
        camAdminRestContext = TestsUtil.createTenantAdminRestContext(global.oaeTests.tenants.cam.host);
        camAnonymousRestContext = TestsUtil.createTenantRestContext(global.oaeTests.tenants.cam.host);
        gtAdminRestContext = TestsUtil.createTenantAdminRestContext(global.oaeTests.tenants.gt.host);
        gtAnonymousRestContext = TestsUtil.createTenantRestContext(global.oaeTests.tenants.gt.host);   
        globalAdminRestContext = TestsUtil.createGlobalAdminRestContext();     
        return callback();
    });

    describe('initiation transfer', function() {
        /**
         * Test that verifies creation of a transfer
         */
        it('verify transfer values when a transfer is initiated', function(callback) {
            TestsUtil.generateTestUsers(camAdminRestContext, 2, function(err, users, mrvisser, stuartf) {
                assert.ok(!err);
                TransferTestUtil.assertInitiateTransferSucceeds(mrvisser.restContext, mrvisser.user.email, stuartf.user.email, mrvisser.user.id, function(createdTransfer) {
                    // Ensure the returned transfer model is accurate   
                    assert.ok(createdTransfer);
                    assert.equal(createdTransfer.originalEmail, mrvisser.user.email);
                    assert.equal(createdTransfer.targetEmail, stuartf.user.email);
                    assert.equal(createdTransfer.originalUserId, mrvisser.user.id);
                    
                    // Sanity check that the transfer was created
                    TransferTestUtil.assertGetTransferSucceeds(mrvisser.restContext, createdTransfer.originalUserId, function(transfer) {
                        // Ensure the fetched transfer model is consistent with the created one
                        assert.ok(transfer);
                        assert.ok(transfer.code);
                        assert.equal(transfer.status, TransferConstants.status.ONGOING);
                        assert.equal(transfer.originalEmail, mrvisser.user.email);
	                    assert.equal(transfer.targetEmail, stuartf.user.email);
	                    assert.equal(transfer.originalUserId, mrvisser.user.id);
                        return callback();
                    });
                });
            });
        });
    });

    describe('Get transfer', function() {
        /**
         * Test that verifies validation of getting a transfer
         */
        it('verify transfer values when getting a transfer', function(callback) {
            TestsUtil.generateTestUsers(camAdminRestContext, 2, function(err, users, mrvisser, stuartf) {
                assert.ok(!err);
                TransferTestUtil.assertInitiateTransferSucceeds(mrvisser.restContext, mrvisser.user.email, stuartf.user.email, mrvisser.user.id, function(transfer) {
                    // Ensure fetching using an invalid id results in an error
                    TransferTestUtil.assertGetTransferFails(mrvisser.restContext, 'nonexistingid', 404, function() {
                        // Ensure fetching using an empty id results in an error
                        TransferTestUtil.assertGetTransferFails(mrvisser.restContext, '', 404, function() {
                            // Sanity check getting an existing transfer
                            TransferTestUtil.assertGetTransferSucceeds(mrvisser.restContext, transfer.originalUserId, function(fetchedTransfer) {
                                // Ensure the fetched transfer model is consistent with the created one
                                assert.ok(fetchedTransfer);
                                assert.ok(fetchedTransfer.code);
                                assert.equal(fetchedTransfer.originalEmail, mrvisser.user.email);
                                assert.equal(fetchedTransfer.targetEmail, stuartf.user.email);
                                assert.equal(fetchedTransfer.originalUserId, mrvisser.user.id);

                                return callback();
                            });
                        });
                    });
                });
            });
        });

    });

    describe('Cancel transfer', function() {
        /**
         * Test that verifies validation of cancelling a transfer
         */
        it('verify the cancellation of a transfer', function(callback) {
            TestsUtil.generateTestUsers(camAdminRestContext, 2, function(err, users, mrvisser, stuartf) {
                assert.ok(!err);
                TransferTestUtil.assertInitiateTransferSucceeds(mrvisser.restContext, mrvisser.user.email, stuartf.user.email, mrvisser.user.id, function(transfer) {
                    // Ensure deleting works
                    TransferTestUtil.assertCancelTransferSucceeds(mrvisser.restContext, transfer.originalEmail, transfer.code, transfer.originalUserId, function() {
                        // Check if the transfer is canceled
                        TransferTestUtil.assertGetTransferFails(mrvisser.restContext, transfer.originalUserId, 404, function() {
                            return callback();
                        });
                    });
                });
            });
        });
    });

    describe('Complete transfer', function() {
        /**
         * Test that verify all case of transfer
         */
        it('verify all the transfer errors', function(callback) {
            TestsUtil.generateTestUsers(camAdminRestContext, 2, function(err, users, mrvisser, stuartf) {
                assert.ok(!err);
                TransferTestUtil.assertInitiateTransferSucceeds(mrvisser.restContext, mrvisser.user.email, stuartf.user.email, mrvisser.user.id, function(transfer) {
                    // Ensure the returned transfer model is accurate   
                    assert.ok(transfer);
                    // Wrong code
                    TransferTestUtil.assertCompleteTransferFails(stuartf.restContext, mrvisser.user.email, 'wrongTransferCode', stuartf.user.email, stuartf.user.id, 404, function() {
                        // Invalid email origin
                        TransferTestUtil.assertCompleteTransferFails(stuartf.restContext, 'invalideEmail', transfer.code, stuartf.user.email, stuartf.user.id, 400, function() {
                            // Empty email
                            TransferTestUtil.assertCompleteTransferFails(stuartf.restContext, '', transfer.code, stuartf.user.email, stuartf.user.id, 400, function() {
                                // Empty code
                                TransferTestUtil.assertCompleteTransferFails(stuartf.restContext, mrvisser.user.email, '', stuartf.user.email, stuartf.user.id, 400, function() {
                                    // Wrong email origin email
                                    TransferTestUtil.assertCompleteTransferFails(stuartf.restContext, 'wrongemail@email.com', transfer.code, stuartf.user.email, stuartf.user.id, 404, function() {
                                        // There is no data to transfer
                                        TransferTestUtil.assertCompleteTransferFails(stuartf.restContext, mrvisser.user.email, transfer.code, stuartf.user.email, stuartf.user.id, 400, function() {
                                            TransferTestUtil.generateDiscussions(mrvisser.restContext, 'private', 3, function(err, mrvisserdoc) {
                                                assert.ok(!err);
                                                TransferTestUtil.assertCompleteTransferSucceeds(stuartf.restContext, mrvisser.user.email, transfer.code, stuartf.user.email, stuartf.user.id, function(err) {
                                                    assert.ok(!err);
                                                    // Check if the transfer is canceled
                                                    TransferTestUtil.assertGetTransferFails(mrvisser.restContext, transfer.originalUserId, 404, function() {
                                                        return callback();
                                                    });
                                                });
                                            });
                                        });
                                    });
                                });
                            });
                        });
                    });
                });
            });
        });

    });

});
