var fs = require('fs');

////////////////////////////
// Reading folder content //
////////////////////////////

/*
 * Asynchronous read of a file on the path provided, passes file content or error in the callback
 * @param   {String}     path            The path to the file
 * @param   {Function}   callback        Function executed when the file has been read, or failed to be read
 * @param   {Object}     callback.err    Error, if any, returned when done reading the file
 * @param   {Object}     callback.data   Data returned after reading the file completed
 */
module.exports.readFile = function(path, callback) {
    fs.readFile(path, 'utf-8', function(err, data) {
        callback(err, data);
    });
};

// TODO: Remove pointless wrapper
module.exports.pathExists = function(resource, callback) {
    fs.exists(resource, callback);
};

module.exports.getFileListForFolder = function(foldername, callback) {
    exports.pathExists(foldername, function(exists) {
        if (exists) {
            fs.readdir(foldername, function(err, files) {
                var finalFiles = [];
                for (var f = 0; f < files.length; f++) {
                    if (files[f].substring(0, 1) !== '.') {
                        finalFiles.push(files[f]);
                    }
                }
                callback(finalFiles);
            });
        } else {
            callback([]);
        }
    });
};
