var ActivityAPI = require('oae-activity');
var ActivityConstants = require('oae-activity/lib/constants').ActivityConstants;
var ActivityModel = require('oae-activity/lib/model');
var Context = require('oae-context').Context;
var PreviewConstants = require('oae-preview-processor/lib/constants');

var ZencoderAPI = require('oae-zencoder/lib/api');

ZencoderAPI.on(PreviewConstants.EVENTS.PREVIEWS_FINISHED, function(content, revision, status) {
    // Add the previews status.
    // The actual images will be added by the content activity entity transformer
    // Needs to be done here as the Zencoder previews are created separately from the oae-preview-processor
    content.previews = content.previews || {};
    content.previews.status = status;

    var millis = Date.now();
    var actorResource = new ActivityModel.ActivitySeedResource('system', 'system', null);
    var objectResource = new ActivityModel.ActivitySeedResource('content', content.id, {'content': content});
    var activitySeed = new ActivityModel.ActivitySeed(PreviewConstants.EVENTS.PREVIEWS_FINISHED, millis, ActivityConstants.verbs.CREATE, actorResource, objectResource);

    var ctx = new Context(content.tenant);
    ActivityAPI.postActivity(ctx, activitySeed);
});
