var OAE = require('oae-util/lib/oae');
var OaeServer = require('oae-util/lib/server');
var log = require('oae-logger').logger('zencoder-rest');

var ZencoderAPI = require('./api');

OaeServer.addSafePathPrefix('/api/zencoder/encodingNotification');

/**
 * @REST postZencoderNotification
 *
 * Send notification when encoding job is finished
 *
 * @Api         private
 * @Server      tenant
 * @Method      POST
 * @Path        /zencoder/encodingNotification
 * @BodyParam   {notification}    body         Upload metadata from Zencoder
 * @Return      {void}
 * @HttpResponse                  200          Encoding completed successfully
 */
OAE.tenantRouter.on('post', '/api/zencoder/encodingNotification', function(req, res) {
    // Tell Zencoder we received the notification or they'll try to resend it
    res.send(202, {message: 'Notification received'});

    ZencoderAPI.encodingCompleted(req.body, function(err) {
        if (err) {
            log().error('Could not save video previews', err);
            return;
        }

        log().info('Video preview processing completed successfully');
        return;
    });
});
