var assert = require('assert');

var ConfigTestUtil = require('oae-config/lib/test/util');

/**
 * Enable the Zencoder configuration for a particular tenant
 *
 * @param  {RestContext}    tenantAdminRestContext  The rest context of a tenant admin who will enable Zencoder
 * @param  {String}         apiKey                  The API key id for the Zencoder requests
 * @param  {String}         output                  The URL of the Zencoder server to configure
 * @param  {String}         watermark               The watermark to emboss on the encoded videos
 * @param  {Function}       callback                Standard callback function
 * @throws {AssertionError}                         Thrown if an error occurs trying to configure the tenant
 */
var enableZencoder = module.exports.enableZencoder = function(tenantAdminRestContext, apiKey, output, watermark, callback) {
    var update = {
        'oae-zencoder/zencoder/output': output,
        'oae-zencoder/zencoder/apiKey': apiKey,
        'oae-zencoder/zencoder/watermark': watermark
    };

    ConfigTestUtil.updateConfigAndWait(tenantAdminRestContext, null, update, function(err) {
        assert.ok(!err);
        return callback();
    });
};
